// Function Node: Split into Gherkin + Test Cases (CSV + HTML) cleanly
const src = items.find(i => i.json.formattedOutput || (i.json.message && i.json.message.content));
const output = src?.json.formattedOutput || src?.json.message?.content || "";

if (!output || !output.trim()) return [];

// Split gherkin and test cases
const parts = output.split(/#{1,}\s*Test\s*Cases/i);
let gherkinPart = (parts[0] || "").trim();
let testCasePart = (parts[1] || "").trim();

// ✅ Clean gherkin: remove ```gherkin, ``` and ### Gherkin Scenarios
gherkinPart = gherkinPart
  .replace(/```gherkin/g, "")
  .replace(/```/g, "")
  .replace(/#{1,3}\s*Gherkin Scenarios/i, "")
  .trim();

// ✅ Clean testCasePart: remove bold markers and redundant markdown tables
testCasePart = testCasePart
  .replace(/\*\*Test\s*Cases?:\*\*/gi, "")
  .replace(/^#+\s*Test\s*Cases/i, "")
  .trim();

// Extract markdown table rows for test cases
const lines = testCasePart.split(/\r?\n/);
const tableLines = lines
  .map(l => l.trim())
  .filter(l => l.startsWith("|") && l.includes("|") && !/^\|\s*-+\s*\|/.test(l));

let testCases = [];
let testCasesHtml = "";

if (tableLines.length > 1) {
  // Parse headers
  const rawHeaders = tableLines.shift().split("|").map(h => h.trim()).filter(Boolean);
  const normalize = (h) => {
    const s = h.toLowerCase().replace(/\s+/g, "");
    if (s.includes("id")) return "TestCaseID";
    if (s.includes("name")) return "TestCaseName";
    if (s.includes("precond")) return "Precondition";
    if (s.includes("step")) return "Steps";
    if (s.includes("expect")) return "ExpectedResult";
    return h || "Column";
  };
  const headers = rawHeaders.map(normalize);

  // Parse rows
  testCases = tableLines.map((line) => {
    const cells = line.split("|").slice(1, -1).map(c => c.trim());
    const obj = {};
    headers.forEach((key, i) => {
      let val = cells[i] || "";

      if (key === "Steps") {
        // ✅ Strip duplicate numbers & re-number
        const parts = val
          .split(/<br\s*\/?>|\n/)
          .map(s => s.replace(/^\s*[\d]+[\.\)\-]?\s*/, "").trim())
          .filter(Boolean);

        val = parts.map((s, stepIdx) => `${stepIdx + 1}. ${s}`).join("\n");
      } else {
        val = val
          .split(/<br\s*\/?>|\n/gi)
          .map(s => s.trim())
          .filter(Boolean)
          .join("\n");
      }
      obj[key] = val;
    });
    return obj;
  });

  // ✅ Build neat HTML table for Text flow
  const headerRow = `<tr>${rawHeaders.map(c => `<th>${c}</th>`).join("")}</tr>`;
  const bodyRows = testCases.map(tc =>
    `<tr>${headers.map(h => `<td>${tc[h] || ""}</td>`).join("")}</tr>`
  ).join("\n");

  testCasesHtml = `
    <h3><b>Test Cases</b></h3>
    <table border="1" cellpadding="6" cellspacing="0" 
           style="border-collapse: collapse; width: 100%; margin-top: 10px;">
      <thead>${headerRow}</thead>
      <tbody>${bodyRows}</tbody>
    </table>
  `;
}

// Build plain text Gherkin file
const gherkinText = gherkinPart.trim();

// Build CSV for test cases
let testcaseCsv = "";
if (testCases.length > 0) {
  const headers = Object.keys(testCases[0]);
  testcaseCsv = headers.join(",") + "\n";
  testcaseCsv += testCases.map(tc =>
    headers.map(h => `"${(tc[h] || "").replace(/"/g, '""')}"`).join(",")
  ).join("\n");
}

// ✅ Return both CSV (for downloads) and HTML (for UI display)
return [
  {
    json: {
      gherkinScenarios: JSON.stringify(gherkinText),
      testCasesTable: JSON.stringify(testCasesHtml || ""),
      filenames: ["gherkin.feature", "testcases.csv"]
    },
    binary: {
      gherkinFile: {
        data: Buffer.from(gherkinText, "utf8").toString("base64"),
        mimeType: "text/plain",
        fileName: "gherkin.feature"
      },
      testcaseFile: {
        data: Buffer.from(testcaseCsv, "utf8").toString("base64"),
        mimeType: "text/csv",
        fileName: "testcases.csv"
      }
    }
  }
];

// Function Node Code (Text Flow - improved with sanitizer + HTML formatting)
const output = $json.formattedOutput || $json.message?.content || "";

// Split into two sections (gherkin + test cases)
const parts = output.split(/(?:#{1,3}\s*Test\s*Cases|[*_]{0,2}\s*Test\s*Cases[:]*\s*[*_]*)/i);
const gherkinPartRaw = parts[0] || "";
const testCasePart = parts[1] || "";

// --- Clean gherkin part
let gherkinPart = gherkinPartRaw
  .replace(/```gherkin\s*/gi, "")
  .replace(/```/g, "")
  .replace(/#{1,3}\s*Gherkin Scenarios/gi, "")
  .trim();

// --- Extract markdown table rows
const lines = testCasePart.split(/\r?\n/);
let tableLines = lines
  .map(l => l.trim())
  .filter(l => l.startsWith("|") && l.includes("|") && !/^\|[-=]+\|?/.test(l)); 

let testCases = [];
let testCasesHtml = "";

// If markdown table is found → parse it
if (tableLines.length > 1) {
  // Parse headers
  const rawHeaders = tableLines.shift().split("|").map(h => h.trim()).filter(Boolean);
  const normalize = (h) => {
    const s = h.toLowerCase().replace(/\s+/g, "");
    if (s.includes("id")) return "TestCaseID";
    if (s.includes("name")) return "TestCaseName";
    if (s.includes("precond")) return "Precondition";
    if (s.includes("step")) return "Steps";
    if (s.includes("expect")) return "ExpectedResult";
    return h || "Column";
  };
  const headers = rawHeaders.map(normalize);

  // Parse rows
  testCases = tableLines.map(line => {
    const cells = line.split("|").map(c => c.trim()).filter(Boolean);
    const obj = {};
    headers.forEach((key, i) => {
      let val = cells[i] || "";
      if (key === "Steps") {
        const parts = val
          .split(/<br\s*\/?>|\n|(?=\d+\.)/)
          .map(s => s.replace(/^\s*[\d]+[\.\)\-]?\s*/, "").trim())
          .filter(Boolean);
        val = parts.map((s, stepIdx) => `${stepIdx + 1}. ${s}`).join("\n");
      }
      obj[key] = val;
    });
    return obj;
  });

  // Build neat HTML table
  const headerRow = `<tr>${rawHeaders.map(c => `<th>${c}</th>`).join("")}</tr>`;
  const bodyRows = testCases.map(tc =>
    `<tr>${headers.map(h => `<td>${tc[h] || ""}</td>`).join("")}</tr>`
  ).join("\n");

  testCasesHtml = `

    <table border="1" cellpadding="6" cellspacing="0" 
           style="border-collapse: collapse; width: 100%; margin-top: 10px;">
      <thead>${headerRow}</thead>
      <tbody>${bodyRows}</tbody>
    </table>
  `;
}

// --- Sanitizer Fallback (if no markdown table was found)
if (!testCasesHtml) {
  const regex = /(TC\d+)\s*[\-:|]?\s*(.*?)\s*(?:\||\n)([\s\S]*?)(?=TC\d+|$)/gi;
  let fallbackCases = [];
  let m;
  while ((m = regex.exec(testCasePart)) !== null) {
    fallbackCases.push({
      TestCaseID: m[1],
      TestCaseName: m[2].trim(),
      Steps: m[3].trim()
    });
  }

  if (fallbackCases.length > 0) {
    const headers = Object.keys(fallbackCases[0]);
    const headerRow = `<tr>${headers.map(c => `<th>${c}</th>`).join("")}</tr>`;
    const bodyRows = fallbackCases.map(tc =>
      `<tr>${headers.map(h => `<td>${tc[h] || ""}</td>`).join("")}</tr>`
    ).join("\n");

    testCasesHtml = `
      <h3><b>Test Cases</b></h3>
      <table border="1" cellpadding="6" cellspacing="0" 
             style="border-collapse: collapse; width: 100%; margin-top: 10px;">
        <thead>${headerRow}</thead>
        <tbody>${bodyRows}</tbody>
      </table>
    `;
  }
}

// ✅ Return JSON-safe strings
return [
  {
    gherkinScenarios: JSON.stringify(gherkinPart),
    testCasesTable: JSON.stringify(testCasesHtml || "")
  }
];
